package org.example.hw.hw1;public class test {    public static void main(String[] args) {        String str = "?1 + 3? = 59";        System.out.printf("Given the equation: %s", str);        System.out.println();        System.out.println("Result: " + resString(str));    }    public static String resString(String str) {        int value1 = str.indexOf('?');        int value2 = str.lastIndexOf('?');        StringBuilder stringBuilder = new StringBuilder(str);        for (int i = 0; i <= 9; i++) {            char temp1 = (char) (i + '0');            stringBuilder.setCharAt(value1, temp1);            for (int j = 0; j < 9; j++) {                char temp2 = (char) (j + '0');                stringBuilder.setCharAt(value2, temp2);                String result = stringBuilder.toString();                if (parseDigit(result)) {                    return result;                }            }        }        return "No solution";    }    public static boolean parseDigit(String res) {        String[] temp = res.split(" ");        int left = Integer.parseInt(temp[0].trim());        int right = Integer.parseInt(temp[2].trim());        int sum = Integer.parseInt(temp[4].trim());        return left + right == sum;    }}